import React from 'react';
import { Box, Typography } from '@mui/material';

const Footer = () => {
  return (
    <Box 
      sx={{ 
        position: 'fixed', // Fija el footer
        bottom: 0, // Asegura que se alinee en la parte inferior
        width: '100%', // Cubre el ancho completo de la página
        textAlign: 'center', 
        padding: 2, 
        backgroundColor: '#1976d2', 
        color: 'white' 
      }}
    >
      <Typography variant="body2">© 2024 Galería Virtual de Arte. Todos los derechos reservados.</Typography>
    </Box>
  );
};

export default Footer;
