import React, { useState, useEffect } from 'react';

const ArtModal = ({ art, onClose }) => {
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState([]);

  useEffect(() => {
    // Cargar los comentarios desde localStorage al abrir el modal
    const storedComments = JSON.parse(localStorage.getItem(art.id)) || [];
    setComments(storedComments);
  }, [art]);

  const handleCommentSubmit = () => {
    if (comment.trim()) {
      const newComments = [...comments, comment];
      setComments(newComments);
      localStorage.setItem(art.id, JSON.stringify(newComments)); // Guardar los comentarios en localStorage
      setComment(''); // Limpiar el campo de comentario
    }
  };

  return (
    <div style={modalStyles}>
      <div style={modalContentStyles}>
        <button onClick={onClose} style={closeButtonStyles}>Cerrar</button>
        <div style={modalBodyStyles}>
          <div style={modelStyles}>
            {/* Mostrar el modelo 3D grande */}
            <img src={art.model} alt={art.name} style={{ width: '300px', height: '300px' }} />
          </div>
          <div style={infoStyles}>
            <h2>{art.name}</h2>
            <p>{art.description}</p>

            {/* Secci√≥n de comentarios */}
            <div>
              <textarea 
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                placeholder="Deja tu comentario..."
                rows="4"
                style={textareaStyles}
              />
              <button onClick={handleCommentSubmit} style={commentButtonStyles}>
                Comentar
              </button>

              <div style={commentsSectionStyles}>
                <h3>Comentarios:</h3>
                <ul>
                  {comments.map((com, index) => (
                    <li key={index} style={commentItemStyles}>{com}</li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Estilos del modal
const modalStyles = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

const modalContentStyles = {
  backgroundColor: 'white',
  padding: '20px',
  borderRadius: '10px',
  width: '80%',
  maxWidth: '900px',
  display: 'flex',
};

const closeButtonStyles = {
  position: 'absolute',
  top: '10px',
  right: '10px',
  background: 'transparent',
  border: 'none',
  fontSize: '20px',
  cursor: 'pointer',
};

const modalBodyStyles = {
  display: 'flex',
  flexDirection: 'row',
};

const modelStyles = {
  flex: 1,
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

const infoStyles = {
  flex: 1,
  padding: '10px',
};

const textareaStyles = {
  width: '100%',
  padding: '10px',
  borderRadius: '5px',
  marginTop: '10px',
  marginBottom: '10px',
};

const commentButtonStyles = {
  backgroundColor: '#007bff',
  color: 'white',
  padding: '10px',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
};

const commentsSectionStyles = {
  marginTop: '20px',
};

const commentItemStyles = {
  padding: '5px 0',
};

export default ArtModal;
