import React, { useState } from 'react';
import { Modal, Button, TextField, Box, Typography } from '@mui/material';
import { motion } from 'framer-motion';

const ArtModal = ({ art, onClose }) => {
  const [comment, setComment] = useState('');

  const handleCommentSubmit = () => {
    const comments = JSON.parse(localStorage.getItem('comments')) || {};
    comments[art.id] = comments[art.id] ? [...comments[art.id], comment] : [comment];
    localStorage.setItem('comments', JSON.stringify(comments));
    setComment('');
  };

  return (
    <Modal open={true} onClose={onClose}>
      <Box
        sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: 400,
          bgcolor: 'background.paper',
          boxShadow: 24,
          p: 4,
          borderRadius: 2,
        }}
      >
        <motion.div initial={{ y: -100 }} animate={{ y: 0 }} transition={{ duration: 0.5 }}>
          <Typography variant="h6">{art.name}</Typography>
          <Typography sx={{ mt: 2 }}>{art.description}</Typography>
        </motion.div>
        <TextField
          fullWidth
          label="Deja un comentario"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          sx={{ mt: 2 }}
        />
        <Button variant="contained" onClick={handleCommentSubmit} sx={{ mt: 2 }}>Enviar Comentario</Button>
      </Box>
    </Modal>
  );
};

export default ArtModal;
