import React, { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import ArtModal from './ArtModal';
import ArtMesh from './ArtMesh';

const artworks = [
  { id: 1, name: 'Retro Computer Setup (FREE)', description: '...', model: '/assets/1/scene.gltf' },
  { id: 2, name: 'CRT TV', description: '...', model: '/assets/3/scene.gltf' },
  { id: 3, name: 'Obra 3', description: '...', model: '/assets/2/scene.gltf' },
];

const Gallery = () => {
  const [selectedArt, setSelectedArt] = useState(null);

  return (
    <div style={galleryContainerStyles}>
      {artworks.map((art) => (
        <div key={art.id} style={artCardStyles} onClick={() => setSelectedArt(art)}>
          <Suspense fallback={<div>Cargando...</div>}>
            <Canvas style={canvasStyles} camera={{ position: [0, 0, 5] }}>
              <ambientLight intensity={0.6} />
              <pointLight position={[10, 10, 10]} />
              <OrbitControls enableZoom={false} />
              <ArtMesh art={art} />
            </Canvas>
          </Suspense>
          <div style={artNameStyles}>{art.name}</div>
        </div>
      ))}
      {selectedArt && (
        <ArtModal art={selectedArt} onClose={() => setSelectedArt(null)} />
      )}
    </div>
  );
};

const galleryContainerStyles = {
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'center',
  padding: '20px',
  gap: '20px',
};

const artCardStyles = {
  width: '300px',
  height: '400px',
  backgroundColor: '#fff',
  borderRadius: '10px',
  overflow: 'hidden',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
};

const canvasStyles = {
  height: '80%',
};

const artNameStyles = {
  padding: '10px',
  textAlign: 'center',
  backgroundColor: '#333',
  color: '#fff',
};

export default Gallery;
