import React, { useState, useEffect } from 'react';
import { Modal, Button, TextField, Box, Typography } from '@mui/material';

const ArtModal = ({ art, onClose }) => {
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState([]);

  // Cargar los comentarios existentes del localStorage al montar el modal
  useEffect(() => {
    const storedComments = JSON.parse(localStorage.getItem('comments')) || {};
    setComments(storedComments[art.id] || []);
  }, [art.id]);

  const handleCommentSubmit = () => {
    const newComments = [...comments, comment];
    const storedComments = JSON.parse(localStorage.getItem('comments')) || {};
    storedComments[art.id] = newComments;
    localStorage.setItem('comments', JSON.stringify(storedComments));
    setComments(newComments);
    setComment('');
  };

  return (
    <Modal open={true} onClose={onClose}>
      <Box
        sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: 400,
          bgcolor: 'background.paper',
          boxShadow: 24,
          p: 4,
          borderRadius: 2,
        }}
      >
        <Typography variant="h6">{art.name}</Typography>
        <Typography sx={{ mt: 2 }}>{art.description}</Typography>
        
        <TextField
          fullWidth
          label="Deja un comentario"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          sx={{ mt: 2 }}
        />
        <Button variant="contained" onClick={handleCommentSubmit} sx={{ mt: 2 }}>
          Enviar Comentario
        </Button>

        <Box sx={{ mt: 3 }}>
          <Typography variant="body2">Comentarios:</Typography>
          {comments.map((comment, index) => (
            <Typography key={index} variant="body2" sx={{ mt: 1 }}>
              - {comment}
            </Typography>
          ))}
        </Box>
      </Box>
    </Modal>
  );
};

export default ArtModal;
