import React, { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html } from '@react-three/drei';
import ArtModal from './ArtModal';

const artworks = [
  { id: 1, name: 'Obra 1', description: 'Descripción de la obra 1', model: '/assets/scene.gltf' },
  { id: 2, name: 'Obra 2', description: 'Descripción de la obra 2', model: '/assets/compu.gltf' },
  { id: 3, name: 'Obra 3', description: 'Descripción de la obra 3', model: '/assets/tv.gltf' },
];

const Gallery = () => {
  const [selectedArt, setSelectedArt] = useState(null);

  const handleArtClick = (art) => {
    setSelectedArt(art);
  };

  return (
    <div 
      style={{ 
        height: '100vh', 
        backgroundColor: '#f5f5f5', 
        pointerEvents: selectedArt ? 'none' : 'auto' // Desactiva las interacciones con el fondo cuando el modal está abierto
      }}
    >
      <div style={galleryStyles}>
        {artworks.map((art) => (
          <div
            key={art.id}
            style={artCardStyles}
            onClick={() => handleArtClick(art)}
          >
            <Suspense fallback={<div>Loading...</div>}>
              <Canvas style={{ height: '200px' }} camera={{ position: [15, 20, 15], zoom: 1 }}>
                <ambientLight intensity={0.5} />
                <pointLight position={[35, 35, 0]} intensity={0.4} />
                <pointLight position={[-35, 35, 0]} intensity={0.4} />
                <OrbitControls enableZoom={false} />
                <Html>
                  <div style={artLabelStyles}>{art.name}</div>
                </Html>
              </Canvas>
            </Suspense>
          </div>
        ))}
      </div>

      {selectedArt && (
        <ArtModal
          art={selectedArt}
          onClose={() => setSelectedArt(null)}
        />
      )}
    </div>
  );
};

const galleryStyles = {
  display: 'flex',
  justifyContent: 'center',
  flexWrap: 'wrap',
  gap: '20px',
  padding: '20px',
};

const artCardStyles = {
  width: '300px',
  height: '250px',
  border: '1px solid #ddd',
  borderRadius: '10px',
  overflow: 'hidden',
  cursor: 'pointer',
  position: 'relative',
  backgroundColor: 'white',
};

const artLabelStyles = {
  position: 'absolute',
  bottom: '10px',
  width: '100%',
  textAlign: 'center',
  color: 'white',
  backgroundColor: 'rgba(0, 0, 0, 0.6)',
  padding: '5px',
  fontSize: '16px',
};

export default Gallery;
