import React, { useState, useEffect, Suspense } from 'react';
import ReactDOM from 'react-dom';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, useGLTF } from '@react-three/drei';
import * as THREE from 'three';

const ArtModel = ({ model }) => {
  const { scene } = useGLTF(model);

  useEffect(() => {
    const box = new THREE.Box3().setFromObject(scene);
    const size = new THREE.Vector3();
    box.getSize(size);
    const scale = 1 / Math.max(size.x, size.y, size.z);
    scene.scale.set(scale, scale, scale);
    scene.position.set(0, -size.y * scale / 2, 0);
  }, [scene]);

  return <primitive object={scene} />;
};

const ArtModal = ({ art, onClose }) => {
  const [username, setUsername] = useState('');
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState([]);

  useEffect(() => {
    const storedComments = JSON.parse(localStorage.getItem(art.id)) || [];
    setComments(storedComments);
  }, [art]);

  const handleCommentSubmit = () => {
    if (username.trim() && comment.trim()) {
      const newComment = { user: username, text: comment };
      const newComments = [...comments, newComment];
      setComments(newComments);
      localStorage.setItem(art.id, JSON.stringify(newComments));
      setComment('');
    } else {
      alert('Por favor ingresa un nombre de usuario y un comentario.');
    }
  };

  return ReactDOM.createPortal(
    <div style={modalStyles}>
      <div style={modalContentStyles}>
        <div style={modalBodyStyles}>
          <div style={modelStyles}>
            <Canvas camera={{ position: [3, 3, 3], fov: 50 }}>
              <ambientLight intensity={0.6} />
              <pointLight position={[10, 10, 10]} intensity={0.8} />
              <Suspense fallback={<Html>Loading...</Html>}>
                <ArtModel model={art.model} />
              </Suspense>
              <OrbitControls />
            </Canvas>
          </div>
          <div style={infoStyles}>
            <h2 style={titleStyles}>{art.name}</h2>
            <p style={descriptionStyles}>{art.description}</p>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Nombre de usuario"
              style={inputStyles}
            />
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Deja tu comentario..."
              style={textareaStyles}
            />
            <button onClick={handleCommentSubmit} style={commentButtonStyles}>
              Comentar
            </button>
            <div style={commentsSectionStyles}>
              <h3 style={sectionTitleStyles}>Comentarios:</h3>
              <ul>
                {comments.map((com, index) => (
                  <li key={index} style={commentItemStyles}>
                    <strong>{com.user}:</strong> {com.text}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
        <button onClick={onClose} style={backButtonStyles}>Volver</button>
      </div>
    </div>,
    document.body
  );
};

// Estilos mejorados
const modalStyles = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.8)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

const modalContentStyles = {
  backgroundColor: '#f9f9f9',
  borderRadius: '12px',
  padding: '30px',
  width: '80%',
  maxWidth: '800px',
  boxShadow: '0 4px 15px rgba(0, 0, 0, 0.3)',
  animation: 'fadeIn 0.5s ease',
};

const modalBodyStyles = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
};

const titleStyles = {
  color: '#053959',
  fontFamily: '"Poppins", sans-serif',
  marginBottom: '10px',
};

const descriptionStyles = {
  textAlign: 'center',
  color: '#555',
  marginBottom: '20px',
};

const inputStyles = {
  width: '100%',
  padding: '12px',
  borderRadius: '6px',
  border: '1px solid #ccc',
  marginBottom: '10px',
};

const textareaStyles = {
  ...inputStyles,
  height: '100px',
};

const commentButtonStyles = {
  backgroundColor: '#053959',
  color: '#fff',
  padding: '12px 20px',
  borderRadius: '6px',
  cursor: 'pointer',
  transition: 'background-color 0.3s',
};

const commentsSectionStyles = {
  width: '100%',
  marginTop: '20px',
};

const sectionTitleStyles = {
  color: '#053959',
  marginBottom: '10px',
};

const commentItemStyles = {
  marginBottom: '10px',
};

const backButtonStyles = {
  backgroundColor: '#ff4b5c',
  color: 'white',
  border: 'none',
  padding: '10px 20px',
  borderRadius: '6px',
  cursor: 'pointer',
  marginTop: '20px',
};

export default ArtModal;
