import React, { useMemo } from 'react';
import { Typography, Box } from '@mui/material';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';

const Home = () => {
  // Función para generar un color aleatorio en formato hexadecimal
  const getRandomColor = () => {
    const r = Math.floor(Math.random() * 256);
    const g = Math.floor(Math.random() * 256);
    const b = Math.floor(Math.random() * 256);
    return `rgb(${r}, ${g}, ${b})`;
  };

  const randomColor = useMemo(() => getRandomColor(), []); // Generamos el color solo una vez

  return (
    <Box sx={{ padding: 4, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
      <Box sx={{ maxWidth: '50%' }}>
        <Typography variant="h2" component="h1" gutterBottom>
          Bienvenido a la Galería Virtual de Arte
        </Typography>
        <Typography variant="body1" gutterBottom>
          Explora modelos 3D de arte digital innovador, disfruta de una experiencia inmersiva y descubre nuevas perspectivas artísticas.
        </Typography>
      </Box>
      <Box sx={{ width: '50%', height: 400 }}>
        <Canvas>
          <ambientLight />
          <OrbitControls />
          {/* Usamos el color aleatorio generado en el cubo */}
          <mesh>
            <boxGeometry args={[1, 1, 1]} />
            <meshStandardMaterial color={randomColor} />
          </mesh>
        </Canvas>
      </Box>
    </Box>
  );
};

export default Home;
