import React, { useState, useEffect } from 'react';

const ArtModal = ({ art, onClose }) => {
  const [username, setUsername] = useState('');
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState([]);

  useEffect(() => {
    // Cargar los comentarios desde localStorage al abrir el modal
    const storedComments = JSON.parse(localStorage.getItem(art.id)) || [];
    setComments(storedComments);
  }, [art]);

  const handleCommentSubmit = () => {
    if (username.trim() && comment.trim()) {
      const newComment = { user: username, text: comment };
      const newComments = [...comments, newComment];
      setComments(newComments);
      localStorage.setItem(art.id, JSON.stringify(newComments)); // Guardar en localStorage
      setComment(''); // Limpiar el campo
    } else {
      alert('Por favor ingresa un nombre de usuario y un comentario.');
    }
  };

  return (
    <div style={modalStyles}>
      <div style={modalContentStyles}>
        <button onClick={onClose} style={closeButtonStyles}>X</button>
        <div style={modalBodyStyles}>
          <div style={modelStyles}>
            {/* Mostrar el modelo 3D o una representaci√≥n */}
            <img src={art.model} alt={art.name} style={{ width: '300px', height: '300px' }} />
          </div>
          <div style={infoStyles}>
            <h2>{art.name}</h2>
            <p>{art.description}</p>

            {/* Formulario de comentarios */}
            <div>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Nombre de usuario"
                style={inputStyles}
              />
              <textarea
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                placeholder="Deja tu comentario..."
                rows="4"
                style={textareaStyles}
              />
              <button onClick={handleCommentSubmit} style={commentButtonStyles}>
                Comentar
              </button>

              {/* Lista de comentarios */}
              <div style={commentsSectionStyles}>
                <h3>Comentarios:</h3>
                <ul>
                  {comments.map((com, index) => (
                    <li key={index} style={commentItemStyles}>
                      <strong>{com.user}:</strong> {com.text}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Estilos
const modalStyles = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

const modalContentStyles = {
  backgroundColor: 'white',
  padding: '20px',
  borderRadius: '10px',
  width: '80%',
  maxWidth: '900px',
  display: 'flex',
  position: 'relative',
};

const closeButtonStyles = {
  position: 'absolute',
  top: '10px',
  right: '10px',
  background: 'transparent',
  border: 'none',
  fontSize: '20px',
  cursor: 'pointer',
};

const modalBodyStyles = {
  display: 'flex',
  flexDirection: 'row',
};

const modelStyles = {
  flex: 1,
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

const infoStyles = {
  flex: 1,
  padding: '10px',
};

const inputStyles = {
  width: '100%',
  padding: '10px',
  margin: '10px 0',
  borderRadius: '5px',
  border: '1px solid #ccc',
};

const textareaStyles = {
  width: '100%',
  padding: '10px',
  borderRadius: '5px',
  marginTop: '10px',
  marginBottom: '10px',
  border: '1px solid #ccc',
};

const commentButtonStyles = {
  backgroundColor: '#007bff',
  color: 'white',
  padding: '10px',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
};

const commentsSectionStyles = {
  marginTop: '20px',
};

const commentItemStyles = {
  padding: '5px 0',
};

export default ArtModal;
