import React from 'react';
import { AppBar, Toolbar, Typography, Button, IconButton, Drawer, List, ListItem, ListItemText } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import HomeIcon from '@mui/icons-material/Home';
import ImageIcon from '@mui/icons-material/Image';
import ContactMailIcon from '@mui/icons-material/ContactMail';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Navbar = () => {
  const [drawerOpen, setDrawerOpen] = useState(false);

  const menuItems = [
    { text: 'Inicio', icon: <HomeIcon />, path: '/' },
    { text: 'Galería', icon: <ImageIcon />, path: '/gallery' },
    { text: 'Contacto', icon: <ContactMailIcon />, path: '/contact' },
  ];

  return (
    <AppBar position="static">
      <Toolbar>
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }}>
          <Typography variant="h6">Galería Virtual de Arte</Typography>
        </motion.div>
        <div style={{ marginLeft: 'auto', display: 'flex' }}>
          {menuItems.map((item) => (
            <Button 
              key={item.text} 
              color="inherit" 
              startIcon={item.icon} 
              component={Link} 
              to={item.path}
              style={{ marginRight: '10px' }}
            >
              {item.text}
            </Button>
          ))}
        </div>
        <IconButton color="inherit" edge="end" onClick={() => setDrawerOpen(true)} sx={{ display: { xs: 'block', md: 'none' } }}>
          <MenuIcon />
        </IconButton>
      </Toolbar>
      <Drawer anchor="right" open={drawerOpen} onClose={() => setDrawerOpen(false)}>
        <List>
          {menuItems.map((item) => (
            <ListItem button key={item.text} component={Link} to={item.path} onClick={() => setDrawerOpen(false)}>
              <ListItemText primary={item.text} />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </AppBar>
  );
};

export default Navbar;
