import React, { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, useGLTF } from '@react-three/drei';
import ArtModal from './ArtModal';

const artworks = [
  { id: 1, name: 'Obra 1', description: 'Descripción de la obra 1', model: '/assets/1/scene.gltf' },
  { id: 2, name: 'Obra 2', description: 'Descripción de la obra 2', model: '/assets/2/scene.gltf' },
  { id: 3, name: 'Obra 3', description: 'Descripción de la obra 3', model: '/assets/3/scene.gltf' },
];

const ArtMesh = ({ art, onClick }) => {
  const { scene } = useGLTF(art.model); // Cargar el modelo 3D

  // Ajuste de escala para que todos los modelos tengan el mismo tamaño
  scene.scale.set(0.3, 0.3, 0.3);

  return (
    <mesh onClick={onClick}>
      <primitive object={scene} />
    </mesh>
  );
};

const Gallery = () => {
  const [selectedArt, setSelectedArt] = useState(null);

  const handleArtClick = (art) => {
    setSelectedArt(art);
  };

  // Alturas definidas para el navbar y el footer
  const navbarHeight = 64; // Ajusta esto a la altura real de tu navbar
  const footerHeight = 50; // Ajusta esto a la altura real de tu footer

  return (
    <div style={{ height: `calc(100vh - ${navbarHeight + footerHeight}px)`, backgroundColor: '#f5f5f5', position: 'relative' }}>
      {/* Capa de fondo oscurecida */}
      {selectedArt && <div style={overlayStyles} />}

      <div style={galleryStyles}>
        {artworks.map((art) => (
          <div key={art.id} style={artCardStyles} onClick={() => handleArtClick(art)}>
            <Suspense fallback={<div>Loading...</div>}>
              <Canvas style={{ height: '80%' }} camera={{ position: [15, 20, 15], zoom: 1 }}>
                <ambientLight intensity={0.5} />
                <pointLight position={[35, 35, 0]} intensity={0.4} />
                <pointLight position={[-35, 35, 0]} intensity={0.4} />
                <OrbitControls enableZoom={false} />
                <ArtMesh art={art} onClick={() => handleArtClick(art)} />
              </Canvas>
            </Suspense>
            <div style={artNameStyles}>{art.name}</div>
          </div>
        ))}
      </div>

      {selectedArt && (
        <ArtModal
          art={selectedArt}
          onClose={() => setSelectedArt(null)}
        />
      )}
    </div>
  );
};

const overlayStyles = {
  position: 'absolute',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  zIndex: 1,
  pointerEvents: 'none',
};

const galleryStyles = {
  display: 'flex',
  justifyContent: 'center',
  flexWrap: 'wrap',
  gap: '20px',
  padding: '20px',
  position: 'relative',
  marginTop: '20px', // Agregado para dejar un espacio de respeto debajo del navbar
};

const artCardStyles = {
  width: '300px',
  height: '350px',
  border: '1px solid #ddd',
  borderRadius: '10px',
  overflow: 'hidden',
  cursor: 'pointer',
  position: 'relative',
  backgroundColor: 'white',
  display: 'flex',
  flexDirection: 'column',
};

const artNameStyles = {
  height: '20%',
  textAlign: 'center',
  padding: '5px',
  backgroundColor: 'rgba(0, 0, 0, 0.6)',
  color: 'white',
  fontSize: '16px',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

export default Gallery;
