import React, { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, useGLTF } from '@react-three/drei';
import ArtModal from './ArtModal';

const artworks = [
  { id: 1, name: 'Obra 1', description: 'Descripción de la obra 1', model: '/assets/balenciga_glass.glb' },
  { id: 2, name: 'Obra 2', description: 'Descripción de la obra 2', model: '/assets/art2.glb' },
  { id: 3, name: 'Obra 3', description: 'Descripción de la obra 3', model: '/assets/art3.glb' },
];

const Gallery = () => {
  const [selectedArt, setSelectedArt] = useState(null);

  const handleArtClick = (art) => {
    setSelectedArt(art);
  };

  return (
    <div style={{ height: '100vh', backgroundColor: '#f5f5f5' }}>
      <div style={galleryStyles}>
        {artworks.map((art) => (
          <div
            key={art.id}
            style={artCardStyles}
            onClick={() => handleArtClick(art)}
          >
            <Canvas style={{ height: '200px' }}>
              <ambientLight intensity={0.5} />
              <OrbitControls enableZoom={false} />
              <Suspense fallback={<div>Loading...</div>}>
                <ModelLoader path={art.model} />
              </Suspense>
              <Html>
                <div style={artLabelStyles}>{art.name}</div>
              </Html>
            </Canvas>
          </div>
        ))}
      </div>

      {selectedArt && (
        <ArtModal
          art={selectedArt}
          onClose={() => setSelectedArt(null)}
        />
      )}
    </div>
  );
};

const ModelLoader = ({ path }) => {
  const { nodes } = useGLTF(path);
  return <primitive object={nodes.Scene} />;
};

// Estilos
const galleryStyles = {
  display: 'flex',
  justifyContent: 'center',
  flexWrap: 'wrap',
  gap: '20px',
  padding: '20px',
};

const artCardStyles = {
  width: '300px',
  height: '250px',
  border: '1px solid #ddd',
  borderRadius: '10px',
  overflow: 'hidden',
  cursor: 'pointer',
  position: 'relative',
  backgroundColor: 'white',
};

const artLabelStyles = {
  position: 'absolute',
  bottom: '10px',
  width: '100%',
  textAlign: 'center',
  color: 'white',
  backgroundColor: 'rgba(0, 0, 0, 0.6)',
  padding: '5px',
  fontSize: '16px',
};

export default Gallery;
