import React, { useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, useGLTF } from '@react-three/drei';
import ArtModal from './ArtModal';

// Datos de las obras
const artworks = [
  { id: 1, name: 'Obra 1', description: 'Descripción de la obra 1', model: '/assets/balenciga glass.glb' },
  { id: 2, name: 'Obra 2', description: 'Descripción de la obra 2', model: '/assets/art2.glb' },
  { id: 3, name: 'Obra 3', description: 'Descripción de la obra 3', model: '/assets/art3.glb' },
];

// Componente de visualización de la galería
const Gallery = () => {
  const [selectedArt, setSelectedArt] = useState(null);

  return (
    <div style={{ height: '100vh', backgroundColor: '#f5f5f5', display: 'flex', flexDirection: 'column' }}>
      <div style={{ flex: 1 }}>
        <Canvas>
          <ambientLight />
          <OrbitControls />
          {artworks.map((art, index) => (
            <ArtPiece key={art.id} art={art} setSelectedArt={setSelectedArt} position={[index * 3 - 3, 0, 0]} />
          ))}
        </Canvas>
      </div>
      {selectedArt && <ArtModal art={selectedArt} onClose={() => setSelectedArt(null)} />}
    </div>
  );
};

// Componente para cargar y mostrar una pieza de arte
const ArtPiece = ({ art, setSelectedArt, position }) => {
  const { scene } = useGLTF(art.model); // Cargar el modelo 3D usando useGLTF

  return (
    <mesh
      onClick={() => setSelectedArt(art)}
      position={position} // Posiciona las obras a lo largo del eje X
      scale={0.5} // Ajusta el tamaño de las obras
    >
      <primitive object={scene} />
      <Html distanceFactor={10}>
        <div style={{ textAlign: 'center', color: 'white', background: 'rgba(0,0,0,0.5)', padding: '5px' }}>
          {art.name}
        </div>
      </Html>
    </mesh>
  );
};

export default Gallery;
