import React, { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, useGLTF } from '@react-three/drei';
import ArtModal from './ArtModal';

// Artwork data
const artworks = [
  { id: 1, name: 'Obra 1', description: 'Descripción de la obra 1', model: '/assets/balenciga-glass.glb' },
  { id: 2, name: 'Obra 2', description: 'Descripción de la obra 2', model: '/assets/art2.glb' },
  { id: 3, name: 'Obra 3', description: 'Descripción de la obra 3', model: '/assets/art3.glb' },
];

// ArtMesh component
const ArtMesh = ({ art, onClick, position }) => {
  const { scene } = useGLTF(art.model); // Load the 3D model

  return (
    <mesh onClick={onClick} position={position}>
      <primitive object={scene} />
      <Html distanceFactor={10}>
        <div style={{ textAlign: 'center', color: 'white', background: 'rgba(0,0,0,0.5)', padding: '5px' }}>
          {art.name}
        </div>
      </Html>
    </mesh>
  );
};

const Gallery = () => {
  const [selectedArt, setSelectedArt] = useState(null);

  const handleArtClick = (art) => {
    setSelectedArt(art);
  };

  return (
    <div style={{ height: '100vh', backgroundColor: '#f5f5f5' }}>
      <Canvas>
        <ambientLight />
        <OrbitControls />

        {/* Use Suspense to handle async loading of the models */}
        <Suspense fallback={<Html center>Loading models...</Html>}>
          {artworks.map((art, index) => (
            <ArtMesh 
              key={art.id} 
              art={art} 
              onClick={() => handleArtClick(art)} 
              position={[index * 3 - 3, 0, 0]} 
            />
          ))}
        </Suspense>
      </Canvas>

      {selectedArt && (
        <ArtModal
          art={selectedArt}
          onClose={() => setSelectedArt(null)}
        />
      )}
    </div>
  );
};

export default Gallery;
