import React, { useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { Html } from '@react-three/drei';
import ArtModal from './ArtModal';

const artworks = [
  { id: 1, name: 'Obra 1', description: 'Descripción de la obra 1', model: '/assets/art1.glb' },
  { id: 2, name: 'Obra 2', description: 'Descripción de la obra 2', model: '/assets/art2.glb' },
  { id: 3, name: 'Obra 3', description: 'Descripción de la obra 3', model: '/assets/art3.glb' },
];

const Gallery = () => {
  const [selectedArt, setSelectedArt] = useState(null);

  return (
    <div>
      <Canvas>
        {artworks.map((art) => (
          <mesh key={art.id} onClick={() => setSelectedArt(art)}>
            {/* Aquí usarías un loader para cargar el modelo 3D */}
            <Html>{art.name}</Html>
          </mesh>
        ))}
      </Canvas>
      {selectedArt && (
        <ArtModal art={selectedArt} onClose={() => setSelectedArt(null)} />
      )}
    </div>
  );
};

export default Gallery;
