import React from 'react';
import { Typography, Box } from '@mui/material';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

const Home = () => {
  // Cargar el modelo GLB
  const { scene } = useGLTF('/assets/balenciga-glass.glb');

  return (
    <Box sx={{ padding: 4, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
      <Box sx={{ maxWidth: '50%' }}>
        <Typography variant="h2" component="h1" gutterBottom>
          Bienvenido a la Galería Virtual de Arte
        </Typography>
        <Typography variant="body1" gutterBottom>
          Explora modelos 3D de arte digital innovador, disfruta de una experiencia inmersiva y descubre nuevas perspectivas artísticas.
        </Typography>
      </Box>
      <Box sx={{ width: '50%', height: 400 }}>
        <Canvas>
          <ambientLight intensity={0.5} />
          <spotLight position={[10, 10, 10]} angle={0.15} />
          <OrbitControls />
          {/* Carga y muestra el modelo 3D */}
          <primitive object={scene} scale={0.5} position={[0, -1, 0]} />
        </Canvas>
      </Box>
    </Box>
  );
};

export default Home;
