import React, { useState, useRef, useEffect, startTransition } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html } from '@react-three/drei';
import ArtModal from './ArtModal';

const artworks = [
  { id: 1, name: 'Obra 1', description: 'Descripción de la obra 1', model: '/assets/balenciga glass.glb' },
  { id: 2, name: 'Obra 2', description: 'Descripción de la obra 2', model: '/assets/art2.glb' },
  { id: 3, name: 'Obra 3', description: 'Descripción de la obra 3', model: '/assets/art3.glb' },
];

const Gallery = () => {
  const [selectedArt, setSelectedArt] = useState(null);
  const [comments, setComments] = useState(() => {
    // Cargar los comentarios del localStorage si existen
    const storedComments = localStorage.getItem('comments');
    return storedComments ? JSON.parse(storedComments) : [];
  });

  const handleArtClick = (art) => {
    startTransition(() => {
      setSelectedArt(art);
    });
  };

  const handleCommentSubmit = (comment) => {
    const newComments = [...comments, comment];
    setComments(newComments);
    localStorage.setItem('comments', JSON.stringify(newComments)); // Guardar en localStorage
  };

  return (
    <div style={{ height: '100vh', backgroundColor: '#f5f5f5' }}>
      <Canvas>
        <ambientLight />
        <OrbitControls />
        {artworks.map((art) => (
          <mesh key={art.id} onClick={() => handleArtClick(art)} position={[art.id * 2 - 3, 0, 0]}>
            <Html distanceFactor={10}>
              <div style={{ textAlign: 'center', color: 'white', background: 'rgba(0,0,0,0.5)', padding: '5px' }}>
                {art.name}
              </div>
            </Html>
          </mesh>
        ))}
      </Canvas>

      {selectedArt && (
        <ArtModal
          art={selectedArt}
          comments={comments}
          onCommentSubmit={handleCommentSubmit}
          onClose={() => setSelectedArt(null)}
        />
      )}
    </div>
  );
};

export default Gallery;
